func callNameArmFamily32(num int16) string {
	if num > SyscallArmMaxNum32 {
		return SyscallArmUnknownName
	}

	return syscallNumTableArmFamily32[num]
}

func callNumTableIsOkArmFamily32() bool {
	if (len(syscallNumTableArmFamily32) == SyscallArmMaxNum32+1) &&
		syscallNumTableArmFamily32[SyscallArmMaxNum32] == SyscallArmLastName32 {
		return true
	}

	return false
}
